configuration.script:
var report_defaults = Java.type("org.postgresql.pljava.pgxs.ReportScript");
var change_defaults = Java.extend(report_defaults, {
	getOutputName : function () {
		return "apidocs/allclasses-index";
	}
});
report.reportScript = new change_defaults();

var args = java.util.List.of(
	"-doctitle",            "${project.name} ${project.version}",
	"-windowtitle",         "${project.name} ${project.version}",
	"--module",             "org.postgresql.pljava",
	"--module-source-path", "org.postgresql.pljava=${basedir}/src/main/java",
	"-link",                "https://docs.oracle.com/javase/10/docs/api",
	"-d",                   "${basedir}/target/site/apidocs",
	"-author",
	"-use",
	"-version"
);

var tool = javax.tools.ToolProvider.getSystemDocumentationTool();
var task = tool.getTask(null, null, null, null, args, null);
task.call();
